import React from 'react';
import store from 'store'
import isLoggedIn from '../helper/isLoggedIn'
import { InputGroup, InputGroupAddon, InputGroupText, Input, Container, Button, Form, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

import { Redirect } from 'react-router-dom'
import axios from "axios";

axios.defaults.baseURL = 'https://nino-monggovest.herokuapp.com'
// axios.defaults.baseURL = 'http://localhost:8080'
class Login extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      password: '',
      email: ''

    }
    this.changePassword = this.changePassword.bind(this)
    this.changeEmail = this.changeEmail.bind(this)
    this.logInLogic = this.logInLogic.bind(this)

  }



  changePassword(event) {
    this.setState({
      password: event.target.value
    })
  }
  changeEmail(event) {
    this.setState({
      email: event.target.value
    })
  }
  // logInLogic(event) {
  //     event.preventDefault()
  //     this.setState({ error: false });

  //     if (!(this.state.email === 'didik' && this.state.password === '12345')) {
  //         return this.setState({ error: true });
  //     }


  // }
  logInLogic(event) {
    event.preventDefault();
    axios
      .post("/v1/api/user/login", {
        // .post("/api/login", {
        email: this.state.email,
        password: this.state.password
      })
      .then((response) => {
        console.log(response, "the response");
        // if (response.status === 200) {
        localStorage.setItem('JWT_TOKEN', response.data.token)
        store.set('loggedIn', true);
        this.props.history.push('/');
        alert('Anda berhasil masuk. Selamat Datang di Monggovest');
        //   localStorage.setItem('USER_ID', user.userId)
        // console.log('userId yang login', localStorage.getItem('USER_ID'))
        //   this.props.toggle()
        //   this.props.checkAuth()
        // }
      })
      .catch(function (error) {
        console.log("the error", error);
        alert("error", error);
      });
    this.setState({
      email: "",
      password: ""
    })

  }
  render() {

    console.log(isLoggedIn())
    if (isLoggedIn()) {
      return (
        <Redirect to='/investasi' />
      )
    } else {

      return (
        <div>
          <Container style={{ textAlign: 'center' }}>
            <h2 style={{ margin: '30px' }}>Login</h2>
            <Form style={{ width: '400px', margin: 'auto' }}>
              <InputGroup>
                <InputGroupAddon addonType="prepend">Email: </InputGroupAddon>
                <Input onChange={this.changeEmail} placeholder="email" />
              </InputGroup>

              <InputGroup>
                <InputGroupAddon addonType="prepend">Password: </InputGroupAddon>
                <Input onChange={this.changePassword} placeholder="Password" />
              </InputGroup>


              <Button onClick={this.logInLogic}>Submit</Button>
            </Form>

          </Container>
        </div>
      );
    }
  }
};

export default Login;
