import React, { Component } from 'react';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';
import Axios from 'axios'



class CarouselHomepage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeIndex: 0,
      newItems: [],
      items: [
        {
          src: 'https://st2.depositphotos.com/6831718/10129/v/950/depositphotos_101294234-stock-illustration-no-entry-icon-on-dark.jpg',
          altText: 'No data 1',
          caption: 'No data 1'
        }
      ],
      errors: [
        {
          src: 'https://img2.pngdownload.id/20180331/oiq/kisspng-computer-icons-warning-sign-icon-design-exclamation-mark-5abfc7b6c2deb6.1743514215225179427982.jpg',
          altText: 'Error 1',
          caption: 'Error 1'
        }
      ],
      errMessage: {}
    };
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.goToIndex = this.goToIndex.bind(this);
    this.onExiting = this.onExiting.bind(this);
    this.onExited = this.onExited.bind(this);

  }
  componentDidMount() {
    Axios.get('https://nino-monggovest.herokuapp.com/v1/api/investment_terbaru').then((response) => {
      this.setState(state => {
        let structureItems = []
        response.data.data.map(data => {
          structureItems.push({ src: data.gambar[0], altText: data.nama, caption: data.nama })
        })
        console.log(structureItems)
        const newItems = state.newItems.concat(structureItems);
        return {
          newItems
        };
      })
    }).catch(err => {
      this.setState({
        errMessage: err
      })
    })
  }

  onExiting() {
    this.animating = true;
  }

  onExited() {
    this.animating = false;
  }

  next() {
    if (this.animating) return;
    if (this.state.newItems.length > 0) {
      const nextIndex = this.state.activeIndex === this.state.newItems.length - 1 ? 0 : this.state.activeIndex + 1;
      this.setState({ activeIndex: nextIndex });
    } else {
      const nextIndex = this.state.activeIndex === this.state.items.length - 1 ? 0 : this.state.activeIndex + 1;
      this.setState({ activeIndex: nextIndex });
    }
  }

  previous() {
    if (this.animating) return;
    if (this.state.newItems.length > 0) {
      const nextIndex = this.state.activeIndex === 0 ? this.state.newItems.length - 1 : this.state.activeIndex - 1;
      this.setState({ activeIndex: nextIndex });
    } else {
      const nextIndex = this.state.activeIndex === 0 ? this.state.items.length - 1 : this.state.activeIndex - 1;
      this.setState({ activeIndex: nextIndex });
    }
  }

  goToIndex(newIndex) {
    if (this.animating) return;
    this.setState({ activeIndex: newIndex });
  }

  render() {
    const { activeIndex } = this.state;
    let slides
    let indicators
    if (this.state.errMessage.data) {
      slides = this.state.errors.map((error) => {
        return (
          <CarouselItem
            onExiting={this.onExiting}
            onExited={this.onExited}
            key={error.src}
          >
            <img style={{ height: "600px", width: '1500px' }} src={error.src} alt={error.altText} />
            <CarouselCaption captionText={error.caption} captionHeader={error.caption} />
          </CarouselItem>
        );
      });
    } else if (this.state.newItems.length > 0) {
      slides = this.state.newItems.map((item) => {
        return (
          <CarouselItem
            onExiting={this.onExiting}
            onExited={this.onExited}
            key={item.src}
          >
            <img style={{ height: "600px", width: '1500px' }} src={item.src} alt={item.altText} />
            <CarouselCaption captionText={item.caption} captionHeader={item.caption} />
          </CarouselItem>
        );
      });
    } else {
      slides = this.state.items.map((item) => {
        return (
          <CarouselItem
            onExiting={this.onExiting}
            onExited={this.onExited}
            key={item.src}
          >
            <img style={{ height: "600px", width: '1500px' }} src={item.src} alt={item.altText} />
            <CarouselCaption captionText={item.caption} captionHeader={item.caption} />
          </CarouselItem>
        );
      });
    }
    if (this.state.newItems.length>0) {
      indicators=<div><CarouselIndicators items={this.state.newItems} activeIndex={activeIndex} onClickHandler={this.goToIndex} />
      {slides}
      <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
      <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} /></div>

    } else {
      indicators = <div> <CarouselIndicators items={this.state.items} activeIndex={activeIndex} onClickHandler={this.goToIndex} />
      { slides }
      <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
        <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} /></div>

    }
    return (
      <Carousel
        activeIndex={activeIndex}
        next={this.next}
        previous={this.previous}
      >
        <CarouselIndicators items={this.state.newItems} activeIndex={activeIndex} onClickHandler={this.goToIndex} />
      { slides }
      <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
        <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} />
      </Carousel>
    );
  }
}


export default CarouselHomepage;
