import React, {Component} from 'react'
import { Card, Button, CardTitle, CardText, Row, Col, Container } from 'reactstrap';
import Axios from 'axios'

class CardInvestasi extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            investasiTerbaru: [],
            investasiSemua: []
        }

        this.getAllInvestment = this.getAllInvestment.bind(this)
    }
    getAllInvestment() {

    }
    componentDidMount() {
        Axios.get('https://nino-monggovest.herokuapp.com/allinvestment')
            .then(investment => {
                this.setState({
                    investasiSemua: investment.data.data
                })
            })
            .catch(err => {
                console.log(err)
            })
    }
    render() {
        const cards = this.state.investasiTerbaru.map(investasi => {
            return (
                <Col sm="4" key={investasi._id}>
                    <Card body style={{ textAlign: "center" }}>
                        <CardTitle><h5>{investasi.nama}</h5></CardTitle>
                        <img style={{ width: 'auto', height: '200px' }}
                            src={investasi.gambar[0]} alt="" />
                        <CardText>{investasi.ringkasan}</CardText>
                        <Button>Lihat Detil</Button>
                    </Card>
                </Col>

            )
        })

        return (
            <div>
                <Container>

                    <div>
                        <h1 style={{ textAlign: 'center' }}>Investasi</h1>
                    </div>
                    <Row >
                        {cards}
                    </Row>
                    <div>

                        <div className="col" style={{textAlign:'center', margin:'20px 0'}} >
                            <a href="#" onClick={this.getAllInvestment} role='button' className='btn btn-dark btn-lg'>Lihat semua investasi</a>
                        </div>

                    </div>
                </Container>

            </div>

        );
    }
};


export default CardInvestasi
