import React from 'react';
import { Card, Button, CardTitle, CardText, Row, Col, Container } from 'reactstrap';
import Axios from 'axios'

class CardHomepage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            investasiTerbaru: [],
            investasiSemua: []
        }

        this.getAllInvestment = this.getAllInvestment.bind(this)
    }
    getAllInvestment() {
        Axios.get('https://nino-monggovest.herokuapp.com/allinvestment')
            .then(investment => {
                this.setState({
                    investasiSemua: investment.data.data
                })
                console.log(this.state.investasiSemua)
            })
            .catch(err => {
                console.log(err)
            }).then(()=>[
                console.log(this.state.investasiSemua)
            ])
            return console.log(this.state.investasiSemua)
    }
    componentDidMount() {
        Axios.get('https://nino-monggovest.herokuapp.com/v1/api/investment_terbaru')
            .then(invTerbaru => {
                this.setState({
                    investasiTerbaru: invTerbaru.data.data
                })

            }).catch(err => {
                console.log(err)
            })
    }
    render() {
        const cards = this.state.investasiTerbaru.map(investasi => {
            return (
                <Col sm="4" key={investasi._id}>
                    <Card body style={{ textAlign: "center" }}>
                        <CardTitle><h5>{investasi.nama}</h5></CardTitle>
                        <img style={{ width: 'auto', height: '200px' }}
                            src={investasi.gambar[0]} alt="" />
                        <CardText>{investasi.ringkasan}</CardText>
                        <Button>Lihat Detil</Button>
                    </Card>
                </Col>

            )
        })

        return (
            <div>
                <Container>

                    <div>
                        <h1 style={{ textAlign: 'center' }}>Investasi Terbaru</h1>
                    </div>
                    <Row >
                        {cards}
                    </Row>
                    <div>

                        <div className="col" style={{textAlign:'center', margin:'20px 0'}} >
                            <a href="https://nino-monggovest.herokuapp.com/allinvestment" onClick={this.getAllInvestment} role='button' className='btn btn-dark btn-lg'>Lihat semua investasi</a>
                        </div>

                    </div>
                </Container>

            </div>

        );
    }
};

export default CardHomepage;
