import React from 'react';
import store from 'store'
import isLoggedIn from '../helper/isLoggedIn'
import { InputGroup, InputGroupAddon, InputGroupText, Input, Container, Button, Form,Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

import { Redirect } from 'react-router-dom'
class Login extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            password: '',
            email: ''

        }
        this.changePassword = this.changePassword.bind(this)
        this.changeEmail = this.changeEmail.bind(this)
        this.logInLogic = this.logInLogic.bind(this)
        this.toggle = this.toggle.bind(this);
        this.toggleNested = this.toggleNested.bind(this);
        this.toggleAll = this.toggleAll.bind(this);        
    }



  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  toggleNested() {
    this.setState({
      nestedModal: !this.state.nestedModal,
      closeAll: false
    });
  }

  toggleAll() {
    this.setState({
      nestedModal: !this.state.nestedModal,
      closeAll: true
    });
  }

    changePassword(event) {
        this.setState({
            password: event.target.value
        })
    }
    changeEmail(event) {
        this.setState({
            email: event.target.value
        })
    }
    logInLogic() {
        this.setState({ error: false });

        if (!(this.state.email === 'didik' && this.state.password === '12345')) {
            return this.setState({ error: true });
        }

        store.set('loggedIn', true);
        this.props.history.push('/');
    }
    render() {
        const { email, password } = this.state
        console.log(isLoggedIn())
        if (isLoggedIn()) {
            return (
                <Redirect to='/investasi' />
            )
        } else {

            return (
                <div>
                    <Container style={{ textAlign: 'center' }}>
                        <h1>LOGIN</h1>
                        <Form>
                            <InputGroupAddon  addonType="prepend">Email: </InputGroupAddon>
                            <Input  onChange={this.changeEmail} placeholder="email" />

                            <InputGroupAddon addonType="prepend">Password: </InputGroupAddon>
                            <Input onChange={this.changePassword} placeholder="Password" />

                            <Button onClick={this.logInLogic}>Submit</Button>
                        </Form>
                        <Button color="danger" onClick={this.toggle}>{this.props.buttonLabel}</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
          <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
            <br />
            <Button color="success" onClick={this.toggleNested}>Show Nested Modal</Button>
            <Modal isOpen={this.state.nestedModal} toggle={this.toggleNested} onClosed={this.state.closeAll ? this.toggle : undefined}>
              <ModalHeader>Nested Modal title</ModalHeader>
              <ModalBody>Stuff and things</ModalBody>
              <ModalFooter>
                <Button color="primary" onClick={this.toggleNested}>Done</Button>{' '}
                <Button color="secondary" onClick={this.toggleAll}>All Done</Button>
              </ModalFooter>
            </Modal>
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.toggle}>Do Something</Button>{' '}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>
                    </Container>
                </div>
            );
        }
    }
};

export default Login;
