import React, { Component } from 'react';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';
import Axios from 'axios'

const items = this.state.items

class CarouselHomepage extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      activeIndex: 0,
      items:[
        {
          src: 'https://milkmaidmarian.com/wp-content/uploads/2011/10/halfdaypaddock.jpg?w=1024',
          altText: 'Slide 1',
          caption: 'Slide 1'
        },
        {
          src: 'https://i.ytimg.com/vi/qvFuYsndj2w/maxresdefault.jpg',
          altText: 'Slide 2',
          caption: 'Slide 2'
        },
        {
          src: 'https://i.ytimg.com/vi/Qkl5Y3btD7A/maxresdefault.jpg',
          altText: 'Slide 3',
          caption: 'Slide 3'
        }
      ],
      errMessage:{}
     };
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.goToIndex = this.goToIndex.bind(this);
    this.onExiting = this.onExiting.bind(this);
    this.onExited = this.onExited.bind(this);
    this.getInvestasiTerbaru = this.getInvestasiTerbaru.bind(this)

  }

  getInvestasiTerbaru(){
    Axios.get('https://nino-monggovest.herokuapp.com/v1/api/investment_terbaru').then(response=>{
      this.setState({
        items: response.data
      })
    }).catch(err=>{
        this.setState({
          errMessage: err
        })
    }).then(()=>{

    })
  }

  onExiting() {
    this.animating = true;
  }

  onExited() {
    this.animating = false;
  }

  next() {
    if (this.animating) return;
    const nextIndex = this.state.activeIndex === items.length - 1 ? 0 : this.state.activeIndex + 1;
    this.setState({ activeIndex: nextIndex });
  }

  previous() {
    if (this.animating) return;
    const nextIndex = this.state.activeIndex === 0 ? items.length - 1 : this.state.activeIndex - 1;
    this.setState({ activeIndex: nextIndex });
  }

  goToIndex(newIndex) {
    if (this.animating) return;
    this.setState({ activeIndex: newIndex });
  }

  render() {
    const { activeIndex } = this.state;

    const slides = items.map((item) => {
      return (
        <CarouselItem
          onExiting={this.onExiting}
          onExited={this.onExited}
          key={item.src}
        >
          <img style={{ height:"600px", width:'1500px'}} src={item.src} alt={item.altText} />
          <CarouselCaption captionText={item.caption} captionHeader={item.caption} />
        </CarouselItem>
      );
    });

    if(Object.entries(this.state.errMessage).length !== 0 && this.state.errMessage.constructor !== Object){

    }
    return (
      <Carousel
        activeIndex={activeIndex}
        next={this.next}
        previous={this.previous}
      >
        <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={this.goToIndex} />
        {slides}
        <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
        <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} />
      </Carousel>
    );
  }
}


export default CarouselHomepage;
